var lo2 = gt.longitude;
var gr = new GlideRecord("sys_user");
gr.query();
while (gr.next()) {
    if (gr.latitude && gr.longitude) {
        var lt1 = gr.latitude;
        var lo1 = gr.longitude;
        var dis = distnce(lt1, lo1, lt2, lo2);
		//gs.info(""+gr.first_name+dis +"");
		if(dis<600)
			{
            var item = map.addItem(gr);
            item.latitude = String(gr.latitude);
            item.longitude = String(gr.longitude);
            item.dialog_title = gr.getDisplayValue();
            item.icon = "https://maps.google.com/mapfiles/kml/pal3/icon51.png";
            item.icon_width = "32";
            item.icon_height = "32";
			}

    }

}
